	for(int i=0;i<n;i++)

	{

		if(s&(1<<i))

		{

			p=i;

			break;

		}

	}

	for(int t=s;t;t=s&(t-1))

	{

		int nt=t|(1<<p);

		dp[s]=max(dp[s],calc(nt)+dfs(s^nt));

	}

	return dp[s];

}

