#define pb push_back

#define inf 1e9

#define pp pop_back

#define pii pair<int,int>

#define vec vector<int>

#define mp make_pair

using namespace std;

int h[100001],dp[100001];

int ans(int n,int k)

{

  if(n==1)

    return 0;

  if(dp[n]!=-1)

    return dp[n];

  int res=inf;

  for(int i=1;i<=min(k,n-1);i++)

    res=min(res,abs(h[n]-h[n-i])+ans(n-i,k));

  return dp[n]=res;

}

