{

	int ret = 1; used[pos] = true;



	for(int i = 0; i < rings[pos]; i++)

	{

		int r = G[pos][i];



		if(!used[r])

		{

			ret = max(ret, rec(r) + 1);

		}

	}



	used[pos] = false;



	return ret;

}



