	for(int x = a; x <= sz; x += x & -x){

		BIT[x] += w;

	}

}



long long int sum(int a){



	// 1ã‹ã‚‰aã¾ã§ã®è¦ç´ ã®åˆè¨ˆã‚’è¿”ã™ O(log N)



	long long int ret = 0;

	for(int x = a; x > 0; x -= x & -x){

		ret += BIT[x];

	}

	return ret;

