ll dp[100002];

ll jump(ll pos)

{

    if(dp[pos]!=-1)

    return dp[pos];

    if(pos == n)

    return 0;

    if(pos > n)

    return INT_MAX;

    ll ans = 0, prev = INT_MAX;

        for(ll i = 1; i<=k; i++){

            ans = (abs(cost[pos] - cost[pos+i]) +  jump(pos+i));

            prev = min(prev, ans);

        }   

    return dp[pos] = prev;

}







