		int x, y;

		if (d == 0) y = b * 2;

		else y = d * 2 + 1;



		if (c == 0) x = a * 2 + 1;

		else x = c * 2;



	//	cout<<x<<' '<<y<<endl;

	//	cout<<a<<' '<<b<<' '<<c<<' '<<d<<endl;



		g[x].PB(y);

		g[y].PB(x);

		OUT[x]++;

		IN[y]++;

	}



	int c1 = 0, c2 = 0;

	FOR (i, 0, MAX)

	{

		if (U[i]) continue;

		if (SZ(g[i]) == 0) continue;

		L = R = 0;



		dfs(i);



		c1++;

		if (L == 0) c2++;

	}

	//cout<<"*"<<endl;

	if (c2) no();

	cout<<"YES"<<endl;



}

