        cin >> arr[i];



    dp.resize(N, {-1, -1});

    dp[0] = {0, 0};

    solve(N - 1);



    ll prev = 0;

    for (int i = 1; i < N; i++)

    {

        prev = max(prev, arr[dp[i].ff] + arr[dp[i].ss]);

