ll jump(ll pos)

{

    if(dp[pos]!=-1)

    return dp[pos];

    if(pos == n)

    return 0;

    if(pos > n)

    return INT_MAX;

    ll ans = 0, prev = INT_MAX;

        for(ll i = 1; i<=k; i++){

