	#else

	#define getc() t1==t2&&(t2=(t1=buf)+fread(buf,1,N,stdin),t1==t2)?EOF:*t1++

	#endif

	inline int read()

	{

		static int an,f;an=0,f=1;

		static char ch;ch=getc();

