{

    string a = to_string(A), b = to_string(B);



    if (a.at(0) == b.at(b.length() - 1) && a.at(a.length() - 1) == b.at(0))

    {

        return true;

    }



    else

    {

        return false;

    }

}



//先頭と末尾の判定カウント

long long test2(long long A, long long B)

{

    string a = to_string(A), b = to_string(B);



    if (a.at(0) == b.at(b.length() - 1) && a.at(a.length() - 1) == b.at(0))

    {



        if (a.at(0) == a.at(a.length() - 1))

        {

            count1++;

        }



        else

        {

            count2++;

        }

    }

}



