{

    long long N, ans = 0, count3 = 0;

    cin >> N;



    if (N < 100)

    {

        for (long long A = 1; A < N + 1; A++)

        {

            for (long long B = 1; B < N + 1; B++)

            {

                if (test1(A, B))

                {

                    ans++;

                }

            }

        }

    }



    else

    {



        ans += pow(N / 10, 2) + 8;



        if (N > (N % 10) * 10)

        {

            for (int j = (N / 10) * 10 + 1; j < N + 1; j++)

            {

