#include<bits/stdc++.h>

#define int long long

using namespace std;

const int N=16;

int dp[1<<N],a[N][N],n;

int calc(int s)

{

	int sum=0;

	for(int i=0;i<n;i++)

	{

		if(!(s&(1<<i)))

			continue;

		for(int j=i+1;j<n;j++)

			if(s&(1<<j))

				sum+=a[i][j];

	}

