{

	n=read(),h=read();

	for(int i=1,a,b,c,d,x,y;i<=n;++i)

	{

		a=read(),b=read(),c=read(),d=read();

		if(c==0)x=a;

		else x=c+h;

		if(d==0)y=b+h;

		else y=d;

		add(x,y);

	}

	for(int i=1;i<=h;++i)if(dg[i]<0)return printf("NO"),0;

	for(int i=h+1;i<=2*h;++i)if(dg[i]>0)return printf("NO"),0;

