int find(int x)

{

   if(x!=fa[x])

      fa[x]=find(fa[x]);

   return fa[x];

}

void Union(int x,int y)

{

   x=find(x);

   y=find(y);

   fa[x]=y;

}

void work()

{

   int cnt=0;

   for(int i=1;i<=m;i++)

      {

	 int A=edge[i].from,B=edge[i].to;

	 if(find(A)!=find(B))

	    {

	       Union(A,B);

	       ans+=edge[i].len;

	       cnt++;

	    }

