typedef long long LL;

typedef vector<int> VI;

typedef pair<int, int> PII;



const double PI = acos(-1.0);

const int INF = 1000 * 1000 * 1000 + 7;

const LL LINF = INF * (LL) INF;



const int MAX = 1010;



VI g[MAX];

int IN[MAX];

int OUT[MAX];

bool U[MAX];



int L, R;



void no()

{

	cout<<"NO"<<endl;

	exit(0);

}



void dfs(int x)

{

	U[x] = true;



	if (x & 1)

	{

		if (IN[x] > OUT[x]) no();

		R += IN[x] - OUT[x];

	}

	else

	{

		if (OUT[x] > IN[x]) no();

		L += OUT[x] - IN[x];

	}



	FOR (i, 0, SZ(g[x]))

	{

		int to = g[x][i];

		if (U[to]) continue;

		dfs(to);

	}

}



