	void flush(){fwrite(buff,1,T-buff,stdout);T=buff;}

	inline void putc(char ch){if(T==buff+N)flush();*T++=ch;}

	template<typename o>

	inline void print(o x)

	{

		if(x<0)putc('-'),x=-x;

		if(!x)return putc('0'),void();

		static int st[20],tp;

		while(x)st[++tp]=x%10,x/=10;

		while(tp)putc(st[tp]^48),--tp;

	}

