        ii maxi = {bit, 0};

        if (arr[0] > arr[bit])

            swap(maxi.ff, maxi.ss);

        for (int i = n; i >= 0; i--)

        {

            ll check = (1 << i);

            if (bit & check)

            {

                auto c = solve(bit & (~(check)));

                if (c.ff != maxi.ff && c.ff != maxi.ss)

                {

                    if (arr[c.ff] > arr[maxi.ff])

                        maxi.ss = maxi.ff, maxi.ff = c.ff;

                    else if (arr[c.ff] > arr[maxi.ss])

                        maxi.ss = c.ff;

                }



                if (c.ss != maxi.ff && c.ss != maxi.ss)

