#include <cstdio>
#include <vector>
#include <algorithm>
#include <climits>

using namespace std;

typedef long long ll;

ll* ar;
ll* dp;
ll n, k;

ll solve(ll i) {
    if (i == n)
        return 0;
    if (i > n)
        return INT_MAX;
    if (dp[i] != -1)
        return dp[i];

    ll mn = INT_MAX;
    for (ll step = 1; step <= k; ++step) {
        if (i + step <= n) {
            ll cost = abs(ar[i] - ar[i + step]) + solve(i + step);
            mn = min(mn, cost);
        } else {
            break;
        }
    }
    return dp[i] = mn;
}

int main() {
    scanf("%lld %lld", &n, &k);
    ar = new ll[n + 1]; // Dynamic allocation based on actual input size
    dp = new ll[n + 1]; // Dynamic allocation based on actual input size
    fill(dp, dp + n + 1, -1); // Initialize dp with -1

    for (ll i = 1; i <= n; ++i)
        scanf("%lld", &ar[i]);

    printf("%lld\n", solve(1));

    delete[] ar; // Free allocated memory
    delete[] dp; // Free allocated memory
    return 0;
}
