#include <iostream>
#include <vector>
#include <algorithm>
#include <limits>

using namespace std;

using ll = long long;

ll minCostPath(const vector<ll>& arr, ll n, ll cur, ll k2, vector<ll>& memo) {
    if (cur == n - 1) // Reached the last element
        return 0;
    if (cur >= n) // Out of bounds
        return numeric_limits<ll>::max();

    if (memo[cur] != -1)
