```cpp
#include <iostream>
#include <vector>
#include <cstring>
#include <algorithm>

using namespace std;

vector<vector<int>> G;
vector<int> rings;
vector<bool> used;

int iterativeDFS(int start) {
    int maxLength = 0;
    vector<int> stack;
    stack.push_back(start);

    while (!stack.empty()) {
        int pos = stack.back();
        stack.pop_back();

        if (!used[pos]) {
            used[pos] = true;
            maxLength++;
            for (int i = 0; i < rings[pos]; i++) {
                int r = G[pos][i];
                if (!used[r]) {
                    stack.push_back(r);
                }
            }
        }
    }
    return maxLength;
}

int main() {
    while (true) {
        int N, A, B;
        scanf("%d", &N);
        if (N == 0) break;

        G.assign(N, vector<int>());
        rings.assign(N, 0);
        used.assign(N, false);

        for (int i = 0; i < N; i++) {
            scanf("%d %d", &A, &B);
            A--; B--;
            G[A].push_back(B);
            G[B].push_back(A);
            rings[A]++;
            rings[B]++;
        }

        int ret = 0;
        for (int i = 0; i < N; i++) {
            if (rings[i] > 0) {
                ret = max(ret, iterativeDFS(i));
                fill(used.begin(), used.end(), false); // Reset used for the next start node
            }
        }

        printf("%d\n", ret);
    }
    return 0;
}
