```cpp
#include <iostream>
#include <vector>
#include <algorithm>

typedef long long ll;

const long long MOD = 1000000007;

int main() {
    int n;
    std::cin >> n;

    ll* a = new ll[n * n]; // Dynamic allocation for the matrix
    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < n; ++j) {
            std::cin >> a[i * n + j]; // Accessing the allocated array
        }
    }

    std::vector<ll> dp(1 << n, 0); // Initialize dp with zeros

    for (int k = 0; k < (1 << n); ++k) {
        std::vector<int> z;
        for (int i = 0; i < n; ++i) {
            if (k & (1 << i)) z.push_back(i);
        }

        int siz = z.size();
        for (int i = 0; i < siz; ++i) {
            for (int j = i + 1; j < siz; ++j) {
                dp[k] += a[z[i] * n + z[j]]; // Use linear access for pair sums
            }
