                a[i][0] = max1; // Update max
                a[i][1] = max2; // Update second max
            }
        }
    }

    int ans = 0;
    for (int i = 1; i < (1 << n); i++) {
        ans = std::max(ans, a[i][0] + a[i][1]);
    }

    printf("%d\n", ans); // Print the final result

    return 0;
}
