```cpp
#include <iostream>
#include <vector>
#include <algorithm>
#include <limits>

using namespace std;

typedef long long ll;

const ll inf = numeric_limits<ll>::max();

int main() {
    // Use faster I/O
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, k;
    cin >> n >> k;

    vector<int> a(n + 1), dp(n + 1, inf);

    for (int i = 1; i <= n; ++i)
        cin >> a[i];

    dp[1] = 0;

    // Using a deque to maintain the minimum costs efficiently
    deque<int> dq;
    dq.push_back(1); // Initialize with the first index

    for (int i = 2; i <= n; ++i) {
        // Remove elements that are out of the jump range
        while (!dq.empty() && dq.front() < i - k)
            dq.pop_front();

        // Calculate minimum cost to reach dp[i]
        dp[i] = dp[dq.front()] + abs(a[i] - a[dq.front()]);

        // Maintain the deque in a way that it has the indices of the minimum dp values
        while (!dq.empty() && dp[dq.back()] >= dp[i]) 
            dq.pop_back();

        dq.push_back(i);
    }

    cout << dp[n] << '\n';

    return 0;
}
```

