int main() {
    int N, K;
    scanf("%d %d", &N, &K); // Faster input

    vector<int> h(N);
    for (int i = 0; i < N; ++i) {
        scanf("%d", &h[i]); // Faster input
    }

    vector<int> dp(N, INF_COST); // Initialize only for N
    dp[N - 1] = 0;

    for (int i = N - 2; i >= 0; --i) {
        for (int j = 1; j <= K && i + j < N; ++j) { // Avoid out-of-bounds
