#include <cstdio>
#include <cstring>

int main() {
    int k;
    char s[100]; // Preallocate memory for the string

    // Use scanf for faster input
    scanf("%d %99s", &k, s); // Limit input to prevent buffer overflow

    int length = strlen(s); // Precompute the length of the string

    if (length > k) {
        // Use printf for faster output
        printf("%.*s...\n", k, s); // Print the first k characters and "..."
    } else {
        printf("%s\n", s); // Print the entire string
    }

    return 0;
}
```

