int rec(int pos, const vector<vector<int>>& G, vector<bool>& used, const vector<int>& rings) {
    int ret = 1;
    used[pos] = true;

    for (int i = 0; i < rings[pos]; i++) {
        int r = G[pos][i];
        if (!used[r]) {
            ret = max(ret, rec(r, G, used, rings) + 1);
        }
    }

    used[pos] = false;
    return ret;
}

int main() {
    int N, A, B;

    while (true) {
        scanf("%d", &N);
        if (N == 0) { break; }

        vector<int> rings(100, 0);
        vector<bool> used(100, false);
        vector<vector<int>> G(100);

        for (int i = 0; i < N; i++) {
            scanf("%d", &A); A--;
            scanf("%d", &B); B--;
            G[A].push_back(B);
            G[B].push_back(A);
            rings[A]++;
            rings[B]++;
        }

        int nodes = 0;
        for (int i = 0; i < 100; i++) {
            if (rings[i]) {
