Heres the optimized version of the code:

```cpp
#include <iostream>
#include <cmath>
#include <cstring>
#include <vector>
#include <algorithm>

using namespace std;

const int inf = 1e9;
vector<int> pedras;
vector<long long> dp;

long long frog2(int n, int k) {
    dp.assign(n, -1);
    dp[n - 1] = 0; // Base case: no cost to stay at the last stone

    for (int i = n - 2; i >= 0; --i) {
        long long menor = inf;
        for (int x = 1; x <= k && (i + x) < n; ++x) {
            long long cost = dp[i + x] + abs(pedras[i] - pedras[i + x]);
            menor = min(menor, cost);
        }
        dp[i] = menor;
    }

    return dp[0];
}

int main() {
    int n, k;
    cin >> n >> k;
    pedras.resize(n);
    for (int i = 0; i < n; i++) {
        cin >> pedras[i];
    }

    cout << frog2(n, k) << endl;
    return 0;
}
```

