#include <algorithm>
#include <cstdio>
#include <vector>
#include <cstring>

using namespace std;

typedef long long ll;
typedef vector<int> vi;

const int mx = 100009;

int n, k;
vector<int> arr;
vector<int> dp;

void init() {
    #ifdef OnlineJudge
    freopen("input.txt", "r", stdin);
    freopen("output.txt", "w", stdout);
    #endif
}

int solve(int idx) {
    if (idx == n - 1) return 0;

    if (dp[idx] != -1) return dp[idx];

    int ans = 1e9;
    for (int i = idx + 1, cnt = 0; cnt < k && i < n; i++, cnt++) {
        int choice = solve(i) + abs(arr[idx] - arr[i]);
        ans = min(choice, ans);
    }

    return dp[idx] = ans;
}

int main() {
    init();
    scanf("%d %d", &n, &k);
    arr.resize(n);
    dp.assign(n, -1);

    for (int i = 0; i < n; i++) {
        scanf("%d", &arr[i]);
    }

    printf("%d\n", solve(0));
    return 0;
}
```

