#include <cstdio>
#include <vector>
#include <algorithm>
#include <cmath>

using namespace std;

int main() {
    int n, k;
    scanf("%d %d", &n, &k);

    vector<int> a(n), cal(n, 1e9 + 7);
    cal[0] = 0;

    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
