	inline void print(o x)

	{

		if(x<0)putc('-'),x=-x;

		if(!x)return putc('0'),void();

		static int st[20],tp;

		while(x)st[++tp]=x%10,x/=10;

		while(tp)putc(st[tp]^48),--tp;

