		if(c==0)x=a;

		else x=c+h;

		if(d==0)y=b+h;

		else y=d;

		add(x,y);

	}

	for(int i=1;i<=h;++i)if(dg[i]<0)return printf("NO"),0;

	for(int i=h+1;i<=2*h;++i)if(dg[i]>0)return printf("NO"),0;

	queue<int>q;

	for(int i=1;i<=h;++i)if(dg[i]>0)q.push(i);

	int cnt=0;

	while(!q.empty())

