```cpp
#include <cstdio>   // For scanf and printf
#include <cstring>  // For strlen

int main() {
    int k;
    char s[10001]; // Assuming a maximum length for the string

    // Use scanf for faster input
    scanf("%d %s", &k, s);

    // Store the length of the string to avoid repeated calls to strlen
    int length = strlen(s);

    // Check if the string's length is greater than k
    if (length > k) {
        // Print the first k characters and append "..."
        printf("%.*s...", k, s);
