#include <bits/stdc++.h>

using namespace std;

typedef long long ll;



void DFS(int& ans, int m, int n, int q, vector<int>& A, const vector<int>& a, const vector<int>& b, const vector<int>& c, const vector<int>& d)

{

	if (A.size() == n) {

		int tmp = 0;

		for (int i = 0; i < q; i++) {

			if (A[b[i]] - A[a[i]] == c[i]) tmp += d[i];

		}

		ans = max(ans, tmp);

		return;

	}



	int start = A.empty() ? 1 : A.back();

	for (int i = start; i <= m; i++) {

		A.push_back(i);

		DFS(ans, m, n, q, A, a, b, c, d);

		A.pop_back();

	}

}



int main()

{

	int n, m, q;

	cin >> n >> m >> q;

	vector<int> a(q), b(q), c(q), d(q);

	for (int i = 0; i < q; i++) {

		cin >> a[i] >> b[i] >> c[i] >> d[i];

		a[i]--, b[i]--;

	}



	int ans = 0;

	vector<int> A;

	DFS(ans, m, n, q, A, a, b, c, d);

	cout << ans << endl;

}

