inline void chmin(ll& a, ll b) {
    if (a > b) {
        a = b;
    }
}

int main() {
    std::scanf("%lld %lld", &N, &K); // Faster input
    for (ll i = 0; i < N; i++) std::scanf("%lld", &h[i]); // Faster input

    std::fill(dp, dp + N, INF); // Initialize only the needed part
    dp[0] = 0;

    for (ll i = 0; i < N; i++) {
        for (ll j = i + 1; j <= std::min(i + K, N - 1); j++) { // Bound check for j
