```cpp
#include <iostream>
#include <vector>
#include <cmath>
#include <cstdio>
#include <algorithm>

using namespace std;

#define ll long long

ll solve(int n, int c, const vector<ll>& v) {
    vector<ll> dp(n, LLONG_MAX);
    dp[n - 1] = 0; // Base case

    for (int i = n - 2; i >= 0; --i) {
        dp[i] = dp[i + 1] + abs(v[i + 1] - v[i]); // Step of 1
        for (int k = 2; k <= c; ++k) {
            if (i + k < n) {
                dp[i] = min(dp[i], dp[i + k] + abs(v[i + k] - v[i])); // Step of k
            }
        }
    }
    return dp[0];
}

int main() {
    int n, c;
    scanf("%d %d", &n, &c);

    vector<ll> v(n);
    for (int i = 0; i < n; i++) {
        scanf("%lld", &v[i]);
    }

    ll result = solve(n, c, v);
    printf("%lld\n", result);
    return 0;
}
```

