            case 'G': g++; break;
            case 'B': b++; break;
        }
    }

    ans = r * g * b;

    // Restructure the nested loop for valid triples
    for (ll len = 1; len <= n; len++) {
        for (ll j = 1; j <= n - 2 * len; j++) {
            if (s[j] != s[j + len] && s[j] != s[j + 2 * len] && s[j + len] != s[j + 2 * len]) {
                ans--;
            }
        }
    }

    // Use printf for output
    printf("%lld\n", ans);
    return 0;
}

