```cpp
#include <bits/stdc++.h>
using namespace std;

const int N = 16;

int dp[1 << N], a[N][N], n;

// Cached sum of pairs for bitmask s
int calc(int s) {
    static int sum_cache[1 << N]; // Cache results for each subset
    if (sum_cache[s] != -1) return sum_cache[s];

    int sum = 0;
    for (int i = 0; i < n; i++) {
        if (!(s & (1 << i))) continue;
        for (int j = i + 1; j < n; j++) {
            if (s & (1 << j)) sum += a[i][j];
        }
    }
    return sum_cache[s] = sum; // Store computed sum
}

int dfs(int s) {
    if (dp[s] != -1) return dp[s];
    if (s == 0) return dp[s] = 0;

    dp[s] = 0;
    int p;

    // Find the first set bit
    for (int i = 0; i < n; i++) {
        if (s & (1 << i)) {
            p = i;
            break;
        }
    }

    // Iterate over all submasks to find the maximum
    for (int t = s; t; t = s & (t - 1)) {
        int nt = t | (1 << p);
        dp[s] = max(dp[s], calc(nt) + dfs(s ^ nt));
    }

    return dp[s];
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(0);  // Disable synchronization with C stdio for faster I/O
    cin >> n;
    for (int i = 0; i < n; i++)
        for (int j = 0; j < n; j++)
            cin >> a[i][j];

    memset(dp, -1, sizeof(dp));
    vector<int> sum_cache(1 << N, -1); // Initialize cache
    cout << dfs((1 << n) - 1) << endl;

    return 0;
}
