{
    string a;
    scanf("%s", &a); // Use scanf to read the string input.

    vector<int> b; // Use dynamic vector instead of fixed-size array.
    int q = 0; // Initialize counter for stack-like behavior.

    for (char c : a) // Range-based for loop for better readability.
