    int s_index = 0; // Index to track the current position in the output array

    // Use scanf for faster input
    scanf("%s", ent);

    for (int i = 0; ent[i] != '\0'; i++) { // Loop until end of string
        if (ent[i] == '0' || ent[i] == '1') {
            s[s_index++] = ent[i]; // Append character directly to the array
        } else if (s_index > 0) {
            s_index--; // Equivalent to pop_back operation
        }
    }

    s[s_index] = '\0'; // Null-terminate the result string

    // Use printf for faster output
    printf("%s\n", s);

    return 0;
}
