const int MAXN = 20;

struct number {
    int f, s;
};

int main() {
    int n;
    scanf("%d", &n);

    // Resize array to 2^n dynamically
    number* a = new number[1 << n];

    for (int i = 0; i < (1 << n); i++) {
        int x;
        scanf("%d", &x);
        a[i].f = x;
        a[i].s = 0; // Initialize s to 0
    }

    for (int j = 0; j < n; j++) {
        for (int i = 0; i < (1 << n); i++) {
            if (i >> j & 1) {
                int x = i ^ (1 << j);
                number& ans = a[i]; // Use reference to avoid unnecessary copying
                ans.f = std::max(a[i].f, a[x].f);
                if (a[i].f > a[x].f) {
                    ans.s = std::max(a[i].s, a[x].f);
                } else {
                    ans.s = std::max(a[i].f, a[x].s);
                }
            }
        }
    }

    long long ans = 0; // Use long long to prevent overflow
    for (int i = 1; i < (1 << n); i++) {
        ans = std::max(ans, static_cast<long long>(a[i].f) + a[i].s);
    }

    // Output results in one go for better performance
    printf("%lld\n", ans);

    delete[] a; // Clean up dynamically allocated memory
    return 0;
}
