                        maxi.ss = maxi.ff, maxi.ff = c.ff;
                    else if (arr[c.ff] > arr[maxi.ss])
                        maxi.ss = c.ff;
                }
                if (c.ss != maxi.ff && c.ss != maxi.ss) {
                    if (arr[c.ss] > arr[maxi.ff])
                        maxi.ss = maxi.ff, maxi.ff = c.ss;
                    else if (arr[c.ss] > arr[maxi.ss])
                        maxi.ss = c.ss;
                }
            }
        }
        dp[bit] = {maxi.ff, maxi.ss}; // Store both values in the dp vector
    }
    return {dp[bit][0], dp[bit][1]};
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);

    cin >> n;
    ll N = (1 << n);
    arr.resize(N);

    for (ll i = 0; i < N; i++)
        cin >> arr[i];

    dp.resize(N, {-1, -1}); // Initialize dp with -1
    dp[0] = {0, 0};

    solve(N - 1); // Solve for the maximum

