#include <bits/stdc++.h>
using namespace std;

int main() {
    ios_base::sync_with_stdio(0);
    cin.tie(0);

    long long n, k;
    while (cin >> n >> k) {
        vector<long long> cost(n);
        for (int i = 0; i < n; i++) cin >> cost[i];

        vector<long long> dp(n, INT_MAX);
        dp[0] = 0;

        // Use a deque to maintain the minimum values in the sliding window
        deque<int> dq;
        dq.push_back(0);

        for (int i = 1; i < n; i++) {
            // Remove elements that are out of the current window
            while (!dq.empty() && dq.front() < i - k) {
                dq.pop_front();
            }

            // Calculate the minimum cost to reach the current position
            dp[i] = dp[dq.front()] + abs(cost[i] - cost[dq.front()]);

            // Maintain the deque to keep track of the minimum dp values
            while (!dq.empty() && dp[dq.back()] >= dp[i]) {
                dq.pop_back();
            }
            dq.push_back(i);
        }

        cout << dp[n - 1] << endl;
    }

    return 0;
}

