    for (long long i = 1; i < n; i++) {
        f[i] = LLONG_MAX; // Initialize to a large value
        for (long long j = 1; j <= k; j++) {
            if (i - j >= 0) {
                f[i] = min(f[i], abs(a[i] - a[i - j]) + f[i - j]);
            }
        }
    }

    cout << f[n - 1] << endl; // Output the minimum cost to reach the last stone
    return 0;
}

