vector<bool> visited(100);
vector<int> dp(100, -1);

int rec(int pos) {
    if (dp[pos] != -1) return dp[pos]; // Return cached result
    visited[pos] = true;
    int ret = 1;

    for (int r : G[pos]) {
        if (!visited[r]) {
            ret = max(ret, rec(r) + 1);
        }
    }

    visited[pos] = false;
    return dp[pos] = ret; // Cache the result
}

