bool pre(int key)
{
    int left,right;
    left=0;
    right=n-1;
    int mid;
    while(left<=right)
    {
        mid=(left+right)/2;
        if(a[mid]==key)
            return true;
        if(key>a[mid])
            left=mid+1;
        else if(key<a[mid])
            right=mid-1;
    }
    return false;
}
