#include <iostream>
#include <vector>
//#include <list>
#include <queue>
#include <stack>
//#include <unordered_map>
#include <algorithm>
//#include <string>
#include <cstring>
#include <cmath>
/*
https://vjudge.net/contest/372054#problem/G
Longest path in a directed graph
bfs won't work here as longest path in directed graph is asked
Topological Sort and then dp // i.e. update the dp table according to the topological sort
https://www.geeksforgeeks.org/longest-path-directed-acyclic-graph-set-2/
*/
using namespace std;
void topological_sort(vector<int> a[], int x, vector<bool> &vis, stack<int> &st)
{
	vis[x]=true;
	int j;
	//cout<<"x and siz[x]"<<x+1<<" "<<a[x].size()<<"\n";
	for(int i=0;i<a[x].size();i++)
	{
		j=a[x][i];
		//cout<<j+1<<" "<<vis[j]<<"\n";
		if(vis[j]==false)
		{
			topological_sort(a,j,vis,st);
		}
	}
	st.push(x);
	//cout<<"pushing"<<x+1<<"\n";
	return;
}
int main()
{
	int i,j,k,t;
    //unsigned long long int x,y,z;
    int x,y,z,m,mod;
    int n;
    cin>>n>>m;
	int res=0;
	stack<int> st;
    vector<int> a[n];
    for(i=0;i<m;i++)
    {
    	cin>>x>>y;
    	--x;
    	--y;
    	a[x].push_back(y);
    }
    vector<bool> vis(n, false);
    vector<int> dp(n,0);
    while(!st.empty())
    	st.pop();
    for(i=0;i<n;i++)
    {
    	if(vis[i]==0)
    		topological_sort(a, i, vis, st);
    }
    //cout<<st.size()<<"\n";
    //cout<<"\n";
    while(!st.empty())
    {
    	z=st.top();
    	st.pop();
    	for(j=0;j<a[z].size();j++)
    	{
    		k=a[z][j];
    		if(dp[k]>dp[z]-1)
    		{
    			dp[k]=dp[z]-1;
    			res=max(res,-1*	dp[k]);
    			//cout<<dp[k]<<" ";
    		}
    	}
    }
    cout<<res<<"\n";
    return 0;
}




