#include <bits/stdc++.h>
using namespace std;
int n,m,ans,indeg[100001],dp[100001];
vector<vector<int>> graph;
int main(){
	cin >> n >> m;
	graph.resize(n+1);
	for(int i = 0 ; i < m ; i += 1){
		int a,b;
		cin >> a >> b;
		graph[a].push_back(b);
		indeg[b] += 1;
	}
	queue<int> q;
	for(int i = 1 ; i <= n ; i += 1){
		if(indeg[i] == 0){
			q.push(i);
		}
	}
	vector<int> order;
	while(q.size()){
		int a = q.front();
		q.pop();
		order.push_back(a);
		for(int i : graph[a]){
			indeg[i] -= 1;
			if(indeg[i] == 0){
				q.push(i);
			}
		}
	}
	for(int i : order){
		for(int j : graph[i]){
			dp[j] = max(dp[j],dp[i]+1);
			ans = max(ans,dp[j]);
		}
	}
	cout << ans << endl;
}




