using ll = long long;
#define ff first
#define ss second
const int N = 1e5 + 10;
int a[N];
int n, m;
int memo[N];
set<int> brk;
int mod = 1000000007;
int dp(int p)
{
	if (p == n)
		return 1;
	if (memo[p] != 0) return memo[p];
	if (brk.count(p + 1))
	{
		return memo[p] = dp(p + 2);
	}
	else if (brk.count(p + 2))
	{
		return memo[p] = dp(p + 1);
	}
	else
	{
		return memo[p] = (dp(p + 1) + (p + 2 > n ? 0 : dp(p + 2))) % mod;
	}
}
void solve()
{
	cin>>n>>m;
	for (int i = 0 ; i < m; i++)
		cin>>a[i];	
	sort(a, a + m);
	bool impossible = false;
	for (int i = 0; i < m - 1; i++)
		if (a[i] + 1 == a[i + 1])
			impossible = true;
	if (impossible)
	{
		cout<<0<<endl;
	}
	else
	{
		for (int i = 0; i < m; i++)
			brk.insert(a[i]);
		cout<<dp(0)<<endl;
	}
}
int main()
{
	int t = 1;
	//cin>>t;
	while(t--)
	{
		solve();
	}
}





