	for(int i=0;i<n;i++)

	{

		if(!(s&(1<<i)))

			continue;

		for(int j=i+1;j<n;j++)

			if(s&(1<<j))

				sum+=a[i][j];

	}

	return sum;

}

int dfs(int s)

{

	if(dp[s]!=-1)

		return dp[s];

	if(s==0)

		return dp[s]=0;

	dp[s]=0;

	int p;

	for(int i=0;i<n;i++)

	{

		if(s&(1<<i))

		{

			p=i;

			break;

		}

	}

	for(int t=s;t;t=s&(t-1))

	{

		int nt=t|(1<<p);

		dp[s]=max(dp[s],calc(nt)+dfs(s^nt));

	}

	return dp[s];

}

