int dp[1<<N], a[N][N], n;

int calc(int s) {
    int sum = 0;
    for(int i = 0; i < n; i++) {
        if(s & (1 << i)) {
            for(int j = i + 1; j < n; j++) {
                if(s & (1 << j)) {
                    sum += a[i][j];
                }
            }
        }
    }
    return sum;
}

int dfs(int s) {
    if(dp[s] != -1) return dp[s];
    if(s == 0) return dp[s] = 0;

    dp[s] = 0;
    int p = __builtin_ctz(s); // Get the position of the first set bit

    for(int t = s; t; t = (t - 1) & s) {
        int nt = t | (1 << p);
        dp[s] = max(dp[s], calc(nt) + dfs(s ^ nt));
    }

    return dp[s];
}

